import axios from "axios";
import Swal from "sweetalert2";



// Tạo một instance của axios
const apiClient = axios.create({
  baseURL: "https://localhost:7254/api", // URL cơ bản của API
  timeout: 10000, // Thời gian chờ (ms)
  headers: {
    "Content-Type": "application/json",
  },
});

// Thêm interceptor nếu cần
apiClient.interceptors.request.use(
  (config) => {
    // Thêm token hoặc tùy chỉnh trước khi gửi request
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.log("Câu lệnh vẫn được thực hiện")
    return Promise.reject(error);
  }
);

apiClient.interceptors.response.use(
  (response) => response,
  (error) => {
    // Xử lý lỗi chung
    if (error.response?.status === 401) {
      const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
            confirmButton: "btn btn-success",
            cancelButton: "btn btn-danger ml-2",
        },
        buttonsStyling: false,
    });

    swalWithBootstrapButtons
        .fire({
            title: "Bạn có chắc chắn không?",
            text: `Bạn đang muốn xóa loại sản phẩm: ${Name}`,
            icon: "warning",
            confirmButtonText: "Có!",
            cancelButtonText: "Không!",
            showCancelButton: true,
        })
        .then((confirm) => {
            if (confirm.value) {
                this.$apiClient.delete(`/Product/Delete/${Id}`)
                    .then(()=>{
                        this.$notify("Thao tác thành công"," ","success");
                        this.GetProduct();
                    })
                    .catch(error=>{
                        this.$notify(error.response.data.Message??"Đã xảy ra lỗi",error.response.data.Errors.join('. ')?? " ","error");
                    })
            } else if ( /* Read more about handling dismissals below */ confirm.dismiss === Swal.DismissReason.cancel) return
        });
      //alert("Bạn không có quyển thực hiện chức năng này");
      return 
    }
    return Promise.reject(error);
  }
);

export default apiClient;
